import randomimport numpy as npfrom config import (    TRAFFIC_LIGHT_LAMBDA,    TRAFFIC_DELAY_MIN,    TRAFFIC_DELAY_MAX,    PARKING_LAMBDA_CAR,    PARKING_LAMBDA_BIKE,)def draw_traffic_light_delay():    return np.random.exponential(1 / TRAFFIC_LIGHT_LAMBDA)def draw_traffic_congestion_delay():    x = random.uniform(TRAFFIC_DELAY_MIN, TRAFFIC_DELAY_MAX)    return xdef draw_parking_time(vehicle_type):    if vehicle_type == "car":        x = np.random.exponential(1 / PARKING_LAMBDA_CAR)        return x    x = np.random.exponential(1 / PARKING_LAMBDA_CAR)    return xdef estimate_time(G, route, base_speed_kmh, vehicle_type, n_lights):    travel_time = 0    congestion_delay = 0    # przejazd + korki    for u, v in zip(route[:-1], route[1:]):        data = G[u][v][0]        length = data.get("length", 0)        maxspeed = data.get("maxspeed")        try:            speed = float(maxspeed)        except:            speed = base_speed_kmh        speed_m_s = speed * 1000 / 3600        if speed_m_s > 0:            travel_time += length / speed_m_s        # Obliczamy korek dla tego odcinka        delay = draw_traffic_congestion_delay()        congestion_delay += delay    parking_time = draw_parking_time(vehicle_type)    total = travel_time + congestion_delay + parking_time    return {        "total": total,        "travel": travel_time,        "congestion": congestion_delay,        "parking": parking_time    }